import React, { Component, Fragment } from 'react';
import Thoughts from '../../posts/Thoughts';
import Comments from '../../../data/comments';
import CommentList from '../../comments/CommentList';
import axios from 'axios';


export default class Activity extends Component {
  constructor(props) {
    super(props);

    this.state =
    {
      result : "",
      inputAddress_ : "https://thunk-api-19.herokuapp.com/api/v1/thought/user/",
      myThought_: [],
      userId_: 1,
      view: 'POSTS',
      hi: [1,2,3,4,5],
    };
    this.changeActivityView = this.changeActivityView.bind(this);
  }

  fetchThoughtData()
  {
    axios.get(this.state.inputAddress_+this.state.userId_).then(response =>
    {
      const result = response.data
      this.setState({myThought_: result});
    }).catch(err => console.log(err), this.setState({result: "Not Found"}));
  }
  componentDidMount()
  {
    this.fetchThoughtData();
  }

  changeActivityView(ACTIVITY_VIEW) {
    this.setState({
      view: ACTIVITY_VIEW
    });
  }
  render() {
    console.log(this.state.myThought_);

    return (
      <div className='activity-page content-page'>
        <div className='tab-wrapper'>
          <button
            className='thoughts-btn'
            onClick={this.changeActivityView.bind(this, 'POSTS')}
          >
            Thoughts
          </button>
          <button
            className='comments-btn'
            onClick={this.changeActivityView.bind(this, 'COMMENTS')}
          >
            Comments
          </button>
        </div>
        {this.state.hi.map((x,i)=> <p>hi</p>)}

      </div>
    );
  }
}
