import React, { Component } from 'react';
import Thoughts from '../posts/Thoughts';
import axios from 'axios';

class SearchBar extends Component {
  constructor(props)
  {
    super(props);

    this.state =
    {
      result : "",
      getBestThought : "https://thunk-api-19.herokuapp.com/api/v1/post/best",
      getWorstThought :"https://thunk-api-19.herokuapp.com/api/v1/post/worst",
      getHashTag: "",
      myBest_: [],
      myWorst_: [],
      myHashTag_: [],
      view: 'DEFAULT',
    };
    this.changeSearchView = this.changeSearchView.bind(this);
  }
  changeSearchView(INPUTVIEW)
  {
    this.setState({view: INPUTVIEW});
  }

  fetchThoughtData = ()=>
  {
    axios.get(this.state.getBestThought).then(response =>
    {
      const result = response.data
      this.setState({myBest_: result});
    }).catch(err => console.log(err), this.setState({result: "Not Found"}));

    axios.get(this.state.getWorstThought).then(response =>
    {
      const result = response.data
      this.setState({myWorst_: result});
    }).catch(err => console.log(err), this.setState({result: "Not Found"}));

    // axios.get(this.state.getHashTag).then(response =>
    // {
    //   const result = response.data
    //   this.setState({myHashTag_: result});
    // }).catch(err => console.log(err), this.setState({result: "Not Found"}));

  }
  componentDidMount()
  {
    this.fetchThoughtData();
  }

  render() {
    console.log("BEST",this.state.myBest_);
    console.log("Worst", this.state.myWorst_);

    let contentToDisplay;

    if (this.state.view === 'DEFAULT') {
      contentToDisplay = (

        <div>DEFAULT</div>

      )
    }

    return (
      <div className='searchbar-header'>
        <section className='search-section'>
          <input type='text' placeholder='Search...' />
          <button className='cancel-search'>Cancel</button>
        </section>
        <section className='tabs'>
          <button className='best-tab'>Best</button>
          <button className='hashtag-tab'>#Tag</button>
          <button className='worst-tab'>Worst</button>
        </section>

        <div className = 'test'>
        <p>test</p>
        { contentToDisplay }
        </div>
      </div>


    );
  }
}

export default SearchBar;
